let codeListings=document.querySelectorAll(".highlight > pre");for(let n=0;n<codeListings.length;n++){const i=codeListings[n].querySelector("code"),t=document.createElement("button");t.setAttribute("type","button"),t.onclick=function(){copyCode(i)},t.classList.add("fas","fa-copy");const e=document.createElement("div");e.classList.add("c2c-tooltip"),e.setAttribute("role","tooltip"),e.innerHTML+="Copy to clipboard";const s=document.createElement("div");s.classList.add("click-to-copy");const o=Popper.createPopper(t,e,{modifiers:[{name:"offset",options:{offset:[0,-48]}}]});t.addEventListener("click",()=>{e.innerHTML="Copied!"}),t.addEventListener("mouseenter",()=>{e.setAttribute("show-tooltip",""),o.setOptions(e=>({...e,modifiers:[...e.modifiers,{name:"eventListeners",enabled:!0}]})),o.update()}),t.addEventListener("mouseleave",()=>{e.removeAttribute("show-tooltip"),e.innerHTML="Copy to clipboard",o.setOptions(e=>({...e,modifiers:[...e.modifiers,{name:"eventListeners",enabled:!1}]}))}),s.append(t),s.append(e),codeListings[n].insertBefore(s,i)}function copyCode(e){navigator.clipboard.writeText(e.textContent.trim())}